package nl.logius.digid.app.domain.attest;

import nl.logius.digid.app.domain.authentication.request.AuthenticateRequest;
import nl.logius.digid.app.domain.authentication.request.WidUpgradeRequest;
import org.junit.jupiter.api.Disabled;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.extension.ExtendWith;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.test.context.SpringBootTest;
import org.springframework.test.context.ActiveProfiles;
import org.springframework.test.context.junit.jupiter.SpringExtension;

import java.io.IOException;
import java.util.List;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertTrue;

@ExtendWith(SpringExtension.class)
@ActiveProfiles({ "default", "unit-test" })
@SpringBootTest
class AttestValidationServiceTest {

    @Autowired
    private AttestValidationService service;

    @Test
    void testAndroidValidAttest() throws IOException {
        var params = new WidUpgradeRequest();

        var certificates = List.of(
            "","","",""
        );

        params.setAndroidCertificates(certificates);
        var appPublicKey = service.validate(params, "SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS");
        assertEquals("SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS", appPublicKey);
    }

    @Test
    @Disabled("Certificate is expired so chain verify will fail")
    void testIosValidAttest() throws IOException {
        var params = new WidUpgradeRequest();
        params.setIosResult("");

        var appPublicKey = service.validate(params, "SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS");
        assertEquals("SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS", appPublicKey);
    }

    @Test
    @Disabled
    void testIosValidAssertion() {
        var assertion = "SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS";
        var challenge = "SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS";
        var appPublicKey = "SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS";

        var params = new AuthenticateRequest();
        params.setSignedChallenge(assertion);

        assertTrue(service.validateAssertion(params, challenge, appPublicKey));
    }
}
