package nl.logius.digid.app.domain.ssl;

import com.nimbusds.jwt.JWTParser;
import nl.logius.digid.app.Application;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.extension.ExtendWith;
import org.mockito.Mock;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.boot.test.context.SpringBootTest;
import org.springframework.test.context.ActiveProfiles;
import org.springframework.test.context.junit.jupiter.SpringExtension;

import java.text.ParseException;
import java.util.List;

import static nl.logius.digid.app.domain.ssl.SslCertUtil.getSslCertificateDigiDDomain;
import static nl.logius.digid.app.domain.ssl.SslCertUtil.getSslCertificateEidDomain;
import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.mockito.Mockito.when;

@ExtendWith(SpringExtension.class)
@ActiveProfiles({ "default", "unit-test" })
@SpringBootTest(webEnvironment= SpringBootTest.WebEnvironment.RANDOM_PORT, classes = Application.class)
class SslPinningServiceTest {

    private final String EXAMPLE_JWT = "";

    @Mock
    private SslPinningRepository repository;

    @Value("${ssl.signing_key}")
    private String signingKey;

    private SslPinningService service;

    @BeforeEach
    void setup() {
        service = new SslPinningService(repository, signingKey);
        when(repository.findAll()).thenReturn(List.of(getSslCertificateEidDomain(), getSslCertificateDigiDDomain()));
    }

    @Test
    void getSignedJwtWithPins() throws ParseException {
        var jwt = JWTParser.parse(EXAMPLE_JWT);

        assertEquals("{\"kid\":\"1\",\"alg\":\"ES512\"}", jwt.getHeader().toString());
        assertEquals("", jwt.getJWTClaimsSet().toString());
    }


}
