import static java.time.ZonedDateTime.now;
import static nl.logius.digid.dc.domain.connection.ProtocolType.SAML_COMBICONNECT;
import static nl.logius.digid.dc.domain.connection.ProtocolType.SAML_ROUTERINGSDIENST;
import static nl.logius.digid.dc.domain.metadata.MetadataResponseStatus.*;
import static org.junit.jupiter.api.Assertions.*;
import static org.mockito.ArgumentMatchers.*;
import static org.mockito.Mockito.*;

@ExtendWith(SpringExtension.class)
@ActiveProfiles({"default", "integration-test"})
@SpringBootTest(webEnvironment = SpringBootTest.WebEnvironment.RANDOM_PORT, classes = {Application.class})
class MetadataRetrieverServiceTest {

    @Mock
    private SamlMetadataProcessErrorRepository errorRepositoryMock;
    @Mock
    private SamlMetadataProcessResultRepository resultRepositoryMock;
    @Mock
    private ConnectionService connectionServiceMock;
    @Mock
    private ServiceService serviceServiceMock;
    @Mock
    private CacheManager cacheManager;
    @Mock
    private RedisTemplate redisTemplate;

    @Spy
    private CacheService cacheService = new CacheService(cacheManager, redisTemplate);

    @Spy
    private SamlMetadataResponseMapper samlMetadataResponseMapper = Mappers.getMapper(SamlMetadataResponseMapper.class);

    @Spy
    @InjectMocks
    private MetadataRetrieverService metadataRetrieverServiceMock;

    @Test
    void metadataResponseMapperMapsSuccessResponseTest() {
        String samlData = "samlData";
        Connection connection = newConnection(SAML_COMBICONNECT, true, true, true);
        Service service = newService(true);
        String requestStatus = "OK";

        SamlMetadataResponse metadataResponse = samlMetadataResponseMapper.mapSuccessResponse(samlData, connection, service, requestStatus);
        assertEquals(metadataResponse.getSamlMetadata(), samlData);
        assertEquals(metadataResponse.getFederationName(), connection.getSsoDomain());
        assertEquals(metadataResponse.getMinimumReliabilityLevel(), service.getMinimumReliabilityLevel());
        assertEquals(metadataResponse.getEncryptionIdType(), service.getEncryptionIdType().name());
        assertEquals(metadataResponse.getAppReturnUrl(), service.getAppReturnUrl());
        assertEquals(metadataResponse.getAppActive(), service.getAppActive());
        assertEquals(metadataResponse.getServiceName(), service.getName());
        assertEquals(metadataResponse.getLegacyWebserviceId(), service.getLegacyServiceId());
        assertEquals(metadataResponse.getRequestStatus(), requestStatus);
        assertNull(metadataResponse.getErrorDescription());
        assertEquals(metadataResponse.getServiceUuid(), service.getServiceUuid());
        assertEquals(metadataResponse.getPermissionQuestion(), service.getPermissionQuestion());
        assertEquals(metadataResponse.getProtocolType(), connection.getProtocolType());
    }

    @Test
    void metadataResponseMapperMapsSuccessResponseCombiConnectTest() {
        String samlData = "samlData";
        Connection connection = newConnection(SAML_ROUTERINGSDIENST, true, true, true);
        Service service = newService(true);
        String requestStatus = STATUS_OK.name();

        SamlMetadataResponse metadataResponse = samlMetadataResponseMapper.mapSuccessResponse(samlData, connection, service, requestStatus);
        assertEquals(metadataResponse.getProtocolType(), connection.getProtocolType());
    }

    @Test
    void metadataResponseMapperMapsErrorResponseTest() {
        String requestStatus = CONNECTION_NOT_FOUND.name();
        String errorDescription = CONNECTION_NOT_FOUND.label;

        SamlMetadataResponse metadataResponse = samlMetadataResponseMapper.mapErrorResponse(requestStatus, errorDescription);

        assertNull(metadataResponse.getSamlMetadata());
        assertNull(metadataResponse.getFederationName());
        assertNull(metadataResponse.getMinimumReliabilityLevel());
        assertNull(metadataResponse.getEncryptionIdType());
        assertNull(metadataResponse.getAppReturnUrl());
        assertNull(metadataResponse.getAppActive());
        assertNull(metadataResponse.getServiceName());
        assertNull(metadataResponse.getLegacyWebserviceId());
        assertEquals(metadataResponse.getRequestStatus(), requestStatus);
        assertEquals(metadataResponse.getErrorDescription(), errorDescription);
    }

    @Test
    void getAllSamlMetadataById() {
        when(resultRepositoryMock.findAllByConnectionIdOrderByCreatedAtDesc(1L, PageRequest.of(1, 10))).thenReturn(MetadataProcessHelper.getPageSamlMetadataProcessResult());

        Page<SamlMetadataProcessResult> result = metadataRetrieverServiceMock.getAllSamlMetadataById(1L, 1, 10);

        verify(resultRepositoryMock, times(1)).findAllByConnectionIdOrderByCreatedAtDesc(1L, PageRequest.of(1, 10));
        assertNotNull(result);
    }

    @Test
    void getSamlMetadataById() {
        when(errorRepositoryMock.findBySamlMetadataProcessResultId(1L, PageRequest.of(1, 10))).thenReturn(MetadataProcessHelper.getPageSamlMetadataProcessError());

        Page<SamlMetadataProcessError> result = metadataRetrieverServiceMock.getSamlMetadataById(1L, 1, 10);

        verify(errorRepositoryMock, times(1)).findBySamlMetadataProcessResultId(1L, PageRequest.of(1, 10));
        assertNotNull(result);
    }

    @Test
    void getProcessedMetadata() {
        SamlMetadataProcessResult processResult = new SamlMetadataProcessResult();
        processResult.setMetadata("metadata");
        Optional<SamlMetadataProcessResult> resultOptional = Optional.of(processResult);

        when(resultRepositoryMock.findById(anyLong())).thenReturn(resultOptional);

        String result = metadataRetrieverServiceMock.getProcessedMetadata(1L);

        verify(resultRepositoryMock, times(1)).findById(anyLong());
        assertNotNull(result);
    }

    @Test
    void processedMetadataNotFound() {
        when(resultRepositoryMock.findById(anyLong())).thenReturn(Optional.empty());

        String result = metadataRetrieverServiceMock.getProcessedMetadata(1L);

        verify(resultRepositoryMock, times(1)).findById(anyLong());
        assertNull(result);
    }

    @Test
    void validRequestTest() {
        Connection connection = newConnection(SAML_COMBICONNECT, true, true, true);
        when(connectionServiceMock.getConnectionByEntityId(anyString())).thenReturn(connection);
        when(serviceServiceMock.serviceExists(any(Connection.class), anyString(), anyString())).thenReturn(newService(true));
        doReturn("reducedMetadata").when(metadataRetrieverServiceMock).generateReducedMetadataString(any(Connection.class), anyString());

        SamlMetadataResponse result = metadataRetrieverServiceMock.resolveSamlMetadata(newMetadataRequest());

        assertEquals(STATUS_OK.name(), result.getRequestStatus());
        assertEquals("reducedMetadata", result.getSamlMetadata());
        assertEquals(20, (long) result.getMinimumReliabilityLevel());
        assertEquals(34, (long) result.getLegacyWebserviceId());
        assertEquals("app.nl/return_url", result.getAppReturnUrl());
        assertEquals(true, result.getAppActive());
        assertEquals("Dienst", result.getServiceName());
    }

    @Test
    void connectionNotFoundTest() {
        when(connectionServiceMock.getConnectionByEntityId(anyString())).thenReturn(null);
        SamlMetadataResponse response = metadataRetrieverServiceMock.resolveSamlMetadata(newMetadataRequest());

        assertEquals(CONNECTION_NOT_FOUND.name(), response.getRequestStatus());
        assertEquals(CONNECTION_NOT_FOUND.label, response.getErrorDescription());
    }

    @Test
    void connectionInactiveTest() {
        Connection connection = newConnection(SAML_COMBICONNECT, false, true, true);
        when(connectionServiceMock.getConnectionByEntityId(anyString())).thenReturn(connection);
        SamlMetadataResponse response = metadataRetrieverServiceMock.resolveSamlMetadata(newMetadataRequest());

        assertEquals(CONNECTION_INACTIVE.name(), response.getRequestStatus());
        assertEquals(CONNECTION_INACTIVE.label, response.getErrorDescription());
    }

    @Test
    void organizationInactiveTest() {
        Connection connection = newConnection(SAML_COMBICONNECT, true, false, true);
        when(connectionServiceMock.getConnectionByEntityId(anyString())).thenReturn(connection);
        SamlMetadataResponse response = metadataRetrieverServiceMock.resolveSamlMetadata(newMetadataRequest());

        assertEquals(ORGANIZATION_INACTIVE.name(), response.getRequestStatus());
        assertEquals(ORGANIZATION_INACTIVE.label, response.getErrorDescription());
    }

    @Test
    void organizationRoleInactiveTest() {
        Connection connection = newConnection(SAML_COMBICONNECT, true, true, false);
        when(connectionServiceMock.getConnectionByEntityId(anyString())).thenReturn(connection);
        SamlMetadataResponse response = metadataRetrieverServiceMock.resolveSamlMetadata(newMetadataRequest());

        assertEquals(ORGANIZATION_ROLE_INACTIVE.name(), response.getRequestStatus());
        assertEquals(ORGANIZATION_ROLE_INACTIVE.label, response.getErrorDescription());
    }

    @Test
    void serviceNotFoundTest() {
        Connection connection = newConnection(SAML_COMBICONNECT, true, true, true);
        when(connectionServiceMock.getConnectionByEntityId(anyString())).thenReturn(connection);

        when(serviceServiceMock.serviceExists(any(Connection.class), anyString(), anyString())).thenReturn(null);
        SamlMetadataResponse response = metadataRetrieverServiceMock.resolveSamlMetadata(newMetadataRequest());

        assertEquals(SERVICE_NOT_FOUND.name(), response.getRequestStatus());
        assertEquals(SERVICE_NOT_FOUND.label, response.getErrorDescription());
    }

    @Test
    void serviceInactiveTest() {
        Connection connection = newConnection(SAML_COMBICONNECT, true, true, true);
        when(connectionServiceMock.getConnectionByEntityId(anyString())).thenReturn(connection);

        when(serviceServiceMock.serviceExists(any(Connection.class), anyString(), anyString())).thenReturn(newService(false));
        SamlMetadataResponse response = metadataRetrieverServiceMock.resolveSamlMetadata(newMetadataRequest());

        assertEquals(SERVICE_INACTIVE.name(), response.getRequestStatus());
        assertEquals(SERVICE_INACTIVE.label, response.getErrorDescription());
    }

    @Test
    void emptyRequestTest() {
        SamlMetadataResponse result = metadataRetrieverServiceMock.resolveSamlMetadata(new SamlMetadataRequest());

        assertEquals(CONNECTION_NOT_FOUND.name(), result.getRequestStatus());
        assertEquals(CONNECTION_NOT_FOUND.label, result.getErrorDescription());
    }

    @Test
    void getServiceUUID() throws InitializationException {
        setupParserPool();

        String result = metadataRetrieverServiceMock.getServiceUUID(newConnection(SAML_COMBICONNECT, true, true, true), newMetadataRequest().getServiceEntityId(), 0);

        assertEquals("SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS", result);
    }

    @Test
    void generateReducedMetadataString() throws InitializationException {
        setupParserPool();

        String result = metadataRetrieverServiceMock.generateReducedMetadataString(newConnection(SAML_COMBICONNECT, true, true, true), newMetadataRequest().getServiceEntityId());

        assertEquals("",
            result);
    }

    private SamlMetadataRequest newMetadataRequest() {
        SamlMetadataRequest request = new SamlMetadataRequest();
        request.setConnectionEntityId("urn:connectionEntityId");
        request.setServiceEntityId("SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS");
        request.setServiceUuid("UUID");
        return request;
    }

    private Connection newConnection(ProtocolType protocolType, boolean connectionIsActive, boolean organizationIsActive, boolean organizationRoleIsActive) {
        Connection connection = new Connection();
        connection.setId(1L);
        connection.setMetadataUrl("SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS");
        connection.setEntityId("SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS");
        connection.setSamlMetadata("");
        connection.setSsoDomain("SsoDomain");
        connection.setSsoStatus(true);
        connection.setProtocolType(protocolType);
        connection.setStatus(newStatus(connectionIsActive));
        connection.setOrganization(newOrganization(organizationIsActive));
        connection.setOrganizationRole(newOrganizationRole(organizationRoleIsActive));
        return connection;
    }

    private Organization newOrganization(boolean isActive) {
        Organization organization = new Organization();
        organization.setStatus(newStatus(isActive));
        return organization;
    }

    private OrganizationRole newOrganizationRole(boolean isActive) {
        OrganizationRole organizationRole = new OrganizationRole();
        organizationRole.setStatus(newStatus(isActive));
        return organizationRole;
    }

    private Service newService(boolean isActive) {
        Service service = new Service();
        service.setLegacyServiceId(34L);
        service.setMinimumReliabilityLevel(LevelOfAssurance.MIDDEN.getLabel());
        service.setEncryptionIdType(EncryptionType.BSN);
        service.setEntityId("urn:nl-eid-gdi:1:0:entities:00000009999999999001");
        service.setServiceUuid("UUID");
        service.setAppReturnUrl("app.nl/return_url");
        service.setAppActive(true);
        service.setName("Dienst");
        service.setPermissionQuestion("Permission");
        service.setStatus(newStatus(isActive));

        return service;
    }

    private Status newStatus(boolean isActive) {
        Status status = new Status();
        status.setActive(isActive);
        if (isActive) {
            status.setActiveFrom(now().minusDays(1L));
            status.setActiveUntil(now().plusDays(1L));
        } else {
            status.setActiveFrom(now().plusDays(1L));
            status.setActiveUntil(now().minusDays(1L));
        }
        return status;
    }

    private void setupParserPool() throws InitializationException {
        InitializationService.initialize();
        ParserPool parserPool = XMLObjectProviderRegistrySupport.getParserPool();
        ReflectionTestUtils.setField(metadataRetrieverServiceMock, "parserPool", parserPool);
    }
}
