package nl.logius.digid.dws.service;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.mockito.ArgumentMatchers.any;

import java.io.IOException;
import java.util.Base64;

import org.bouncycastle.asn1.ASN1Sequence;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.extension.ExtendWith;
import org.mockito.InjectMocks;
import org.mockito.Mock;
import org.mockito.Mockito;
import org.springframework.test.context.ActiveProfiles;
import org.springframework.test.context.junit.jupiter.SpringExtension;

import nl.logius.digid.dws.client.ActivateBsnkClient;
import nl.logius.digid.dws.exception.BsnkException;
import nl.logius.digid.dws.util.BsnkUtils;

@ExtendWith(SpringExtension.class)
@ActiveProfiles({ "default", "unit-test" })
public class BsnkServiceTest {

    @Mock
    ActivateBsnkClient bsnkClient;

    @Mock
    BsnkUtils bsnkUtils;

    @InjectMocks
    private BsnkService service;

    @Test
    public void bsnkActivateTest() throws IOException, BsnkException {
        String signedPipBase64 = "";
        String pipbase64 = "";
        ASN1Sequence signedPip = (ASN1Sequence) ASN1Sequence.fromByteArray(Base64.getDecoder().decode(signedPipBase64));
        ASN1Sequence pip = (ASN1Sequence) ASN1Sequence.fromByteArray(Base64.getDecoder().decode(pipbase64));

        Mockito.when(bsnkUtils.signedPipFromPplist(any())).thenReturn(signedPip);
        Mockito.when(bsnkUtils.verifySignedPip(any())).thenReturn(true);
        Mockito.when(bsnkUtils.retrievePipFromSignedPip(any())).thenReturn(pip);

        String result = service.bsnkActivate("PPPPPPPPP");

        assertEquals(pipbase64, result);
    }
}
