import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.extension.ExtendWith;
import org.mockito.Mockito;
import org.mockito.MockitoAnnotations;
import org.springframework.boot.test.context.SpringBootTest;
import org.springframework.http.MediaType;
import org.springframework.http.RequestEntity;
import org.springframework.http.ResponseEntity;
import org.springframework.test.context.ActiveProfiles;
import org.springframework.test.context.junit.jupiter.SpringExtension;

import java.net.URI;
import java.util.List;
import java.util.Map;

import static org.junit.jupiter.api.Assertions.assertEquals;

@ExtendWith(SpringExtension.class)
@ActiveProfiles({ "default", "unit-test" })
@SpringBootTest(webEnvironment = SpringBootTest.WebEnvironment.RANDOM_PORT, classes = { Application.class })
class NIKIntegrationTest extends AuthServiceSetup {
    private final ByteArray kEnc = ByteArray.fromBase64("SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS");
    private final ByteArray kMac = ByteArray.fromBase64("SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS");
    private final EcPrivateKey ephemeralKey = ObjectUtils.deserialize(Base64.decode(
        ""
    ), EcPrivateKey.class);

    @BeforeEach
    public void init() throws Exception {
        // init mocks
        MockitoAnnotations.initMocks(this);

        // load db
        flyway.clean();
        flyway.migrate();
        loadCscaCertificate("nik/tv/csca.crt");
        loadCvCertificate("nik/tv/cvca.cvcert", true);
        loadCvCertificate("nik/tv/dvca.cvcert", false);
        loadCvCertificate("nik/tv/at.cvcert", false);
    }

    @Test
    void testStartRestService() throws Exception {
        createSession(s -> {});

        // request
        RequestEntity<Map<String, Object>> re = RequestEntity.post(new URI(Constants.URL_NIK_START))
            .accept(MediaType.APPLICATION_JSON).contentType(MediaType.APPLICATION_JSON)
            .header("X-Forwarded-For", "127.0.0.1").body(ImmutableMap.of("header", header(),
                "userConsentType", "PIP", "documentType", "oAAAAkcQAQ=="));
        ResponseEntity<String> response = restTemplate.exchange(re, String.class);

        assertEquals(200, response.getStatusCodeValue());
        Map<String, Object> result = mapper.readValue(response.getBody(), new TypeReference<Map<String, Object>>() {
        });
        Map<String, Object> responseData = (Map<String, Object>) result.get("responseData");

        // assert
        assertEquals(sessionId, result.get("sessionId"));
        assertEquals("OK", result.get("status"));
        assertEquals(null, responseData.get("atCert"));
    }

    @Test
    void getPrepareEacRequestRestService() throws Exception {
        createSession(s -> s.setUserConsentType(PolymorphType.PIP));
        Mockito.when(securityFactory.generateKey(Mockito.eq(ephemeralKey.toDomainParameters()))).thenReturn(ephemeralKey);

        // request
        RequestEntity<Map<String, Object>> re = RequestEntity.post(new URI(Constants.URL_NIK_PREPARE_EAC))
            .accept(MediaType.APPLICATION_JSON).contentType(MediaType.APPLICATION_JSON)
            .header("X-Forwarded-For", "127.0.0.1").body(ImmutableMap.of("header", header(),
                "dg14",
                "",
                "SSSSSS", "PPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPP", "SSSSS",
                "",
                "SSSSSSS", "PPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPP"));
        ResponseEntity<String> response = restTemplate.exchange(re, String.class);

        assertEquals(200, response.getStatusCodeValue());
        Map<String, Object> result = mapper.readValue(response.getBody(), new TypeReference<Map<String, Object>>() {
        });
        Map<String, Object> responseData = (Map<String, Object>) result.get("responseData");

        // assert
        assertEquals(sessionId, result.get("sessionId"));
        assertEquals("OK", result.get("status"));
        assertEquals("",
            responseData.get("ephemeralPKey"));
        assertEquals(8, ((List<String>)responseData.get("PPPPP")).size());
        assertEquals(ImmutableList.of("SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS","","SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS","SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS","","SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS","SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS","SSSSSSSSSSSSSSSSSSSSSSSSSSSS"),
            responseData.get("PPPPP"));
    }

    @Test
    void testPreparePcaRequestRestService() throws Exception {
        createSession(s -> {
            s.setEphemeralKey(ephemeralKey);
            s.setIdpicc(ByteArray.fromBase64("PPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPPP"));
            s.setUserConsentType(PolymorphType.PIP);
            s.setAtReference("SSSSSSSSSSSSSSSS");
            s.setkEnc(kEnc);
            s.setkMac(kMac);
        });

        Mockito.when(signatureService.sign(
            Mockito.eq(Base64.decode("SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS")),
            Mockito.eq("SSSSSSSSSSSSSSSS"), Mockito.eq(true)
        )).thenReturn(Base64.decode("SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS"));

        // request
        RequestEntity<Map<String, Object>> re = RequestEntity.post(new URI(Constants.URL_NIK_PREPARE_PCA))
            .accept(MediaType.APPLICATION_JSON).contentType(MediaType.APPLICATION_JSON)
            .header("X-Forwarded-For", "127.0.0.1").body(ImmutableMap.of("header", header(), "counter",
                "7", "apdu", "SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS"));
        ResponseEntity<String> response = restTemplate.exchange(re, String.class);

        assertEquals(200, response.getStatusCodeValue());
        Map<String, Object> result = mapper.readValue(response.getBody(), new TypeReference<Map<String, Object>>() {
        });
        Map<String, Object> responseData = (Map<String, Object>) result.get("responseData");

        // assert
        assertEquals(sessionId, result.get("sessionId"));
        assertEquals("OK", result.get("status"));
        assertEquals(ImmutableList.of(
            "SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS",
            "SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS", "SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS"
            ), responseData.get("PPPPP")
        );
    }

    @Test
    void testGetPolymorphicDataRestService() throws Exception {
        createSession(s -> {
            s.setkEnc(kEnc);
            s.setkMac(kMac);
            s.setUserConsentType(PolymorphType.PIP);

            s.setReturnUrl("http://localhost");
            s.setConfirmId("confirmId");
            s.setConfirmSecret("secret");
        });

        // request
        RequestEntity<Map<String, Object>> re = RequestEntity.post(new URI(Constants.URL_NIK_POLYMORPHICDATA))
            .accept(MediaType.APPLICATION_JSON).contentType(MediaType.APPLICATION_JSON)
            .header("X-Forwarded-For", "127.0.0.1")
            .body(ImmutableMap.of("header", header(), "counter", "10", "apdu",
                ""));
        ResponseEntity<String> response = restTemplate.exchange(re, String.class);

        assertEquals(200, response.getStatusCodeValue());
        Map<String, Object> result = mapper.readValue(response.getBody(), new TypeReference<Map<String, Object>>() {
        });
        Map<String, Object> responseData = (Map<String, Object>) result.get("responseData");

        // assert
        assertEquals(sessionId, result.get("sessionId"));
        assertEquals("OK", result.get("status"));
        assertEquals("OK", responseData.get("result"));
        Mockito.verify(confirmService).sendAssertion(Mockito.eq("http://localhost"), Mockito.eq("confirmId"),
            Mockito.eq("secret"), Mockito.eq(PolymorphType.PIP), Mockito.isA(Confirmation.class));
    }
}
