package nl.logius.digid.eid.service;

import nl.logius.digid.card.ObjectUtils;
import nl.logius.digid.card.crypto.EcPrivateKey;
import nl.logius.digid.eid.Application;
import nl.logius.digid.eid.helpers.AuthServiceSetup;
import nl.logius.digid.eid.models.EidSession;
import nl.logius.digid.eid.models.PolymorphType;
import nl.logius.digid.eid.models.db.Certificate;
import nl.logius.digid.eid.models.rest.app.*;
import nl.logius.digid.sharedlib.model.ByteArray;
import org.bouncycastle.util.encoders.Base64;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.extension.ExtendWith;
import org.mockito.InjectMocks;
import org.mockito.Mockito;
import org.mockito.MockitoAnnotations;
import org.mockito.Spy;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.test.context.SpringBootTest;
import org.springframework.boot.test.mock.mockito.MockBean;
import org.springframework.test.context.ActiveProfiles;
import org.springframework.test.context.junit.jupiter.SpringExtension;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.mockito.ArgumentMatchers.*;
import static org.mockito.Mockito.*;

@ExtendWith(SpringExtension.class)
@ActiveProfiles({ "default", "unit-test" })
@SpringBootTest(webEnvironment = SpringBootTest.WebEnvironment.RANDOM_PORT, classes = { Application.class })
public class RDWServiceTest extends AuthServiceSetup {
    private final EcPrivateKey ephemeralKey = ObjectUtils.deserialize(Base64.decode(
        ""
    ), EcPrivateKey.class);
    private final String localhost = "127.0.0.1";

    @MockBean
    private CVCertificateService cvCertificateServiceMock;

    @Spy
    @InjectMocks
    @Autowired
    private RDWService rdwService;

    @BeforeEach
    public void init() throws Exception {
        // init mocks
        MockitoAnnotations.initMocks(this);

        // load db
        flyway.clean();
        flyway.migrate();
        loadCscaCertificate("rdw/acc/csca.crt");
        loadCvCertificate("rdw/acc/cvca.cvcert", true);
        loadCvCertificate("rdw/acc/dvca.cvcert", false);
        loadCvCertificate("rdw/acc/at001.cvcert", false);
    }

    @Test
    public void validatePolymorphInfoRestServiceTest() {
        EidSession session = new EidSession();
        Certificate certificate = new Certificate();
        certificate.setSubject("Subject");
        certificate.setIssuer("SSSSSSSSSSSSSSSS");
        certificate.setRaw(Base64.decode(""));
        PolyInfoRequest request = new PolyInfoRequest();
        request.setHeader(createRequestHeader());
        request.setCar("SSSSSSSSSSSSSSSS".getBytes());
        request.setEfCardAccess(Base64.decode(""));

        when(securityFactory.generateKey(Mockito.eq(ephemeralKey.toDomainParameters()))).thenReturn(ephemeralKey);
        doReturn(session).when(rdwService).initSession(any(AppRequest.class), eq(localhost), any(AppResponse.class));
        when(cvCertificateServiceMock.getBySubject(isNull())).thenReturn(certificate);
        when(cvCertificateServiceMock.getIssuer(any(Certificate.class))).thenReturn(certificate);

        PolyInfoResponse result = rdwService.validatePolymorphInfoRestService(request, localhost);

        assertEquals("OK", result.getStatus());
        verify(cvCertificateServiceMock, times(1)).getBySubject(isNull());
        verify(cvCertificateServiceMock, times(1)).getIssuer(any(Certificate.class));
    }

    @Test
    public void getDigitalSignatureRestServiceTest() {
        EidSession session = new EidSession();
        session.setAtReference("SSSSSSSSSSSSSSSS");
        session.setEphemeralKey(ephemeralKey);
        session.setIdpicc(ByteArray.fromBase64("SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS"));
        SignatureRequest request = new SignatureRequest();
        request.setHeader(createRequestHeader());
        byte[] signature = "signature".getBytes();

        doReturn(session).when(rdwService).initSession(any(AppRequest.class), eq(localhost), any(AppResponse.class));
        when(signatureService.sign(any(byte[].class), eq("SSSSSSSSSSSSSSSS"), eq(false))).thenReturn(signature);

        SignatureResponse result = rdwService.getDigitalSignatureRestService(request, localhost);

        assertEquals("OK", result.getStatus());
        assertEquals(signature, result.getSignature());
        verify(signatureService, times(1)).sign(any(byte[].class), eq("SSSSSSSSSSSSSSSS"), eq(false));
    }

    @Test
    public void generateSecureAPDUsRestServiceTest() {
        EidSession session = new EidSession();
        session.setAtReference("SSSSSSSSSSSSSSSS");
        session.setEphemeralKey(ephemeralKey);
        session.setKeyReference(2);
        session.setTaVersion(2);
        session.setPaceVersion(2);
        session.setUserConsentType(PolymorphType.PIP);
        session.setIdpicc(ByteArray.fromBase64("SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS"));
        SecApduRequest request = new SecApduRequest();
        request.setHeader(createRequestHeader());
        request.setEfCardSecurity(Base64.decode(""));
        request.setPcaApplicationId(Base64.decode("SSSSSSSSSSSS"));
        request.setRpicc(Base64.decode("SSSSSSSSSSSS"));
        request.setTpicc(Base64.decode("SSSSSSSSSSSS"));
        byte[] signature = "signature".getBytes();

        doReturn(session).when(rdwService).initSession(any(AppRequest.class), eq(localhost), any(AppResponse.class));
        when(signatureService.sign(any(byte[].class), eq("SSSSSSSSSSSSSSSS"), eq(false))).thenReturn(signature);

        SecApduResponse result = rdwService.generateSecureAPDUsRestService(request, localhost);

        assertEquals("OK", result.getStatus());
        assertEquals(3, result.getApdus().size());
    }
}
