package nl.logius.digid.eid.test;

import javax.smartcardio.ResponseAPDU;

import org.bouncycastle.asn1.cms.ContentInfo;
import org.bouncycastle.util.encoders.Base64;

import nl.logius.digid.card.ByteArrayUtils;
import nl.logius.digid.card.apdu.AESSecureMessaging;
import nl.logius.digid.card.apdu.SecureMessaging;
import nl.logius.digid.card.asn1.Asn1ObjectMapper;
import nl.logius.digid.card.crypto.CertificateVerifier;
import nl.logius.digid.card.crypto.CmsVerifier;
import nl.logius.digid.eid.models.EidSession;
import nl.logius.digid.eid.models.asn1.PcaSecurityInfos;
import nl.logius.digid.eid.security.TestSecurityFactory;
import nl.logius.digid.eid.util.KeyUtils;
import nl.logius.digid.pp.crypto.BrainpoolP320r1;
import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.*;

/**
 * Utility class to encrypt last RDW apdu for a different PIP but using same efCardAccess and rpicc
 */
public class ApduEncryptPip {
    public static void main(String[] args) {
        if (args.length < 2) {
            System.err.println("Usage ApduEcnryptPip efCardSecurity pip [rpicc]");
            System.exit(1);
        }
        final byte[] efCardSecurity = Base64.decode(args[0]);
        final byte[] pip = Base64.decode(args[1]);
        final byte[] rpicc = args.length >= 3 ? Base64.decode(args[2]) : null;
        ResponseAPDU response = encrypt(efCardSecurity, pip, rpicc);
        System.out.println(Base64.toBase64String(response.getBytes()));
    }

    public static ResponseAPDU encrypt(byte[] efCardSecurity, byte[] pip, byte rpicc[]) {
        final EidSession session = new EidSession();
        session.setEphemeralKey(new TestSecurityFactory().generateKey(BrainpoolP320r1.DOMAIN_PARAMS));
        KeyUtils.generateSecretKeys(
            session, parseEfCardSecurity(efCardSecurity).getEcPublicKey().getPublicParameters(), rpicc
        );

        final SecureMessaging sm = new AESSecureMessaging(
            session.getkEnc().data, session.getkMac().data, ByteArrayUtils.toByteArray(4, 16)
        );
        final ResponseAPDU plain = responseApdu(pip, 0x9000);
        return sm.encrypt(plain);
    }

    private static PcaSecurityInfos parseEfCardSecurity(byte[] efCardSecurity) {
        return new Asn1ObjectMapper().read(new CmsVerifier(new CertificateVerifier.None()).verifyMessage(
            ContentInfo.getInstance(efCardSecurity), "0.4.0.127.0.7.3.2.1"), PcaSecurityInfos.class
        );
    }

    private static ResponseAPDU responseApdu(byte[] data, int sw) {
        final byte[] bytes = new byte[data.length + 2];
        System.arraycopy(data, 0, bytes, 0, data.length);
        bytes[data.length] = (byte) (sw >>> 8);
        bytes[data.length+1] = (byte) (sw & 0xff);
        return new ResponseAPDU(bytes);
    }

    @Test
    public void shouldCreateValidEncyrptedPip() {
        assertEquals("",
            Base64.toBase64String(encrypt(
                Base64.decode(""),
                Base64.decode("SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS"),
                Base64.decode("SSSSSSSSSSSS")
            ).getBytes())
        );
    }
}
