package nl.logius.digid.saml.domain.logout;

import nl.logius.digid.saml.domain.authentication.ProtocolType;
import nl.logius.digid.saml.domain.credential.SignatureService;
import nl.logius.digid.saml.domain.metadata.DcMetadataService;
import nl.logius.digid.saml.domain.session.FederationSessionRepository;
import nl.logius.digid.saml.domain.session.SamlSession;
import nl.logius.digid.saml.domain.session.SamlSessionRepository;
import nl.logius.digid.saml.domain.session.SamlSessionService;
import nl.logius.digid.saml.exception.SamlValidationException;
import nl.logius.digid.saml.extensions.InitializationExtension;
import org.apache.velocity.app.VelocityEngine;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.extension.ExtendWith;
import org.mockito.Mock;
import org.mockito.junit.jupiter.MockitoExtension;
import org.opensaml.core.xml.config.XMLObjectProviderRegistrySupport;
import org.springframework.context.ApplicationContext;
import org.springframework.mock.web.MockHttpServletRequest;
import org.springframework.mock.web.MockHttpSession;
import org.springframework.test.context.junit.jupiter.SpringExtension;
import org.springframework.test.util.ReflectionTestUtils;

import javax.servlet.http.HttpSession;
import java.util.Optional;

import static org.junit.jupiter.api.Assertions.*;
import static org.mockito.ArgumentMatchers.anyString;
import static org.mockito.Mockito.when;

@ExtendWith({MockitoExtension.class, SpringExtension.class, InitializationExtension.class})
public class LogoutServiceTest {

    private MockHttpServletRequest httpRequestMock;
    private LogoutService logoutService;

    @Mock
    private DcMetadataService dcMetadataServiceMock;
    @Mock
    private SamlSessionService samlSessionServiceMock;
    @Mock
    private SignatureService signatureServiceMock;
    @Mock
    private SamlSessionRepository samlSessionRepositoryMock;
    @Mock
    private ApplicationContext ctxMock;
    @Mock
    private FederationSessionRepository federationSessionRepositoryMock;
    @Mock
    private VelocityEngine velocityEngineMock;

    @BeforeEach
    public void setup() {
        logoutService = new LogoutService(ctxMock, federationSessionRepositoryMock, velocityEngineMock);

        ReflectionTestUtils.setField(logoutService, "dcMetadataService", dcMetadataServiceMock);
        ReflectionTestUtils.setField(logoutService, "signatureService", signatureServiceMock);
        ReflectionTestUtils.setField(logoutService, "parserPool", XMLObjectProviderRegistrySupport.getParserPool());
        ReflectionTestUtils.setField(logoutService, "samlSessionService", samlSessionServiceMock);
        ReflectionTestUtils.setField(logoutService, "samlSessionRepository", samlSessionRepositoryMock);
        ReflectionTestUtils.setField(logoutService, "frontChannel", "SSSSSSSSSSSSSSSSSSSSSSS");
        String idpEntityId = "urn:nl-eid-gdi:1.0:AD:0000000273813120000:entities:0000";
        ReflectionTestUtils.setField(logoutService, "idpEntityId", idpEntityId);

        HttpSession session = new MockHttpSession(null, "SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS");

        httpRequestMock = new MockHttpServletRequest();
        httpRequestMock.setMethod("POST");
        httpRequestMock.setServerName("SSSSSSSSSSSSSSSS");
        httpRequestMock.setRequestURI("saml/v4/idp/requestLogout");
        httpRequestMock.setParameter("SAMLRequest", "");
        httpRequestMock.setRequestedSessionId("SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS");
        httpRequestMock.setSession(session);
    }

    @Test
    public void logoutRequestSuccessful() {
        when(samlSessionRepositoryMock.findById(anyString())).thenReturn(Optional.of(createSamlSession("SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS")));
        assertDoesNotThrow(() -> logoutService.parseLogoutRequest(httpRequestMock));
    }

    @Test
    public void parseLogoutRequestWrongVersion() {
        httpRequestMock.setParameter("SAMLRequest", "");

        Exception exception = assertThrows(SamlValidationException.class,
                () -> logoutService.parseLogoutRequest(httpRequestMock));
        assertEquals("LogoutRequest validation error", exception.getMessage());
    }

    @Test
    public void parseLogoutRequestNoIssueInstant() {
        httpRequestMock.setParameter("SAMLRequest", "");
        Exception exception = assertThrows(SamlValidationException.class,
                () -> logoutService.parseLogoutRequest(httpRequestMock));
        assertEquals("LogoutRequest validation error", exception.getMessage());
    }

    @Test
    public void parseLogoutRequestNoNameID() {
        httpRequestMock.setParameter("SAMLRequest", "");

        Exception exception = assertThrows(SamlValidationException.class,
                () -> logoutService.parseLogoutRequest(httpRequestMock));
        assertEquals("LogoutRequest validation error", exception.getMessage());
    }

    @Test
    public void parseLogoutRequestNoNameIDValue() {
        httpRequestMock.setParameter("SAMLRequest", "");

        Exception exception = assertThrows(SamlValidationException.class,
                () -> logoutService.parseLogoutRequest(httpRequestMock));
        assertEquals("LogoutRequest validation error", exception.getMessage());
    }

    @Test
    public void parseLogoutRequestNoSignature() {
        httpRequestMock.setParameter("SAMLRequest", "");

        Exception exception = assertThrows(SamlValidationException.class,
                () -> logoutService.parseLogoutRequest(httpRequestMock));
        assertEquals("LogoutRequest validation error", exception.getMessage());
    }

    private SamlSession createSamlSession(String nameId) {
        SamlSession samlSession = new SamlSession(900);
        samlSession.setHttpSessionId("SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS");
        samlSession.setAuthenticationLevel(20);
        samlSession.setArtifact("SSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSSS");

        samlSession.setIssuer("urn:nl-eid-gdi:1:0:entities:00000008888888888000");
        samlSession.setConnectionEntityId("urn:nl-eid-gdi:1:0:entities:00000008888888888000");
        samlSession.setServiceEntityId("urn:nl-eid-gdi:1:0:entities:00000009999999999001");
        ReflectionTestUtils.setField(samlSession, "id", nameId);
        samlSession.setProtocolType(ProtocolType.SAML_ROUTERINGSDIENST);
        return samlSession;
    }
}
