require 'nokogiri'
require 'ostruct'

# create a JSON array of one letter
Stel /^ik heb data voor een activatiebrief$/ do
  activation_letter = ""
  @letters = json_to_letter_proxy(activation_letter)
end


# create a JSON array of one letter
Stel /^ik heb data voor een activatiebrief van een geemigreerd persoon$/ do
  activation_letter = ""
  @letters = json_to_letter_proxy(activation_letter)
end




def json_to_letter_proxy(json)
  letter_proxy = [OpenStruct.new(JSON.parse(json))]
  def letter_proxy.find_each
    self.each { |letter| yield letter }
  end
  letter_proxy
end

